contracts/interfaces/ITaskTreasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface ITaskTreasury {
    /// @notice Events ///
    event FundsDeposited(
        address indexed sender,
        address indexed token,
        uint256 indexed amount
    );

    event FundsWithdrawn(
        address indexed receiver,
        address indexed initiator,
        address indexed token,
        uint256 amount
    );

    /// @notice External functions ///

    function depositFunds(
        address receiver,
        address token,
        uint256 amount
    ) external payable;

    function withdrawFunds(
        address payable receiver,
        address token,
        uint256 amount
    ) external;

    function useFunds(
        address token,
        uint256 amount,
        address user
    ) external;

    function addWhitelistedService(address service) external;

    function removeWhitelistedService(address service) external;

    /// @notice External view functions ///

    function gelato() external view returns (address);

    function getCreditTokensByUser(address user)
        external
        view
        returns (address[] memory);

    function getWhitelistedServices() external view returns (address[] memory);

    function userTokenBalance(address user, address token)
        external
        view
        returns (uint256);
}

contracts/libraries/LibShares.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.12;
import {ETH} from "../functions/FUtils.sol";
import {IERC20Extended} from "../interfaces/IERC20Extended.sol";

library LibShares {
    function contractBalance(address _token) internal view returns (uint256) {
        if (_token == ETH) {
            return address(this).balance;
        } else {
            return IERC20Extended(_token).balanceOf(address(this));
        }
    }

    function tokenToShares(
        address _token,
        uint256 _tokenAmount,
        uint256 _totalShares,
        uint256 _totalBalance
    ) internal view returns (uint256) {
        uint256 sharesOfToken;

        uint256 tokenIn18Dp = to18Dp(_token, _tokenAmount);
        uint256 totalBalanceIn18Dp = to18Dp(_token, _totalBalance);

        // credit shares equivalent to token amount
        if (_totalShares == 0 || _totalBalance == 0) {
            sharesOfToken = tokenIn18Dp;
        } else {
            sharesOfToken = divCeil(
                tokenIn18Dp * _totalShares,
                totalBalanceIn18Dp
            );
        }

        return sharesOfToken;
    }

    function to18Dp(address _token, uint256 _amount)
        internal
        view
        returns (uint256)
    {
        if (_token == ETH) return _amount;
        uint256 decimals = IERC20Extended(_token).decimals();

        if (decimals < 18) {
            return _amount * 10**(18 - decimals);
        } else {
            return _amount / 10**(decimals - 18);
        }
    }

    function sharesToToken(
        uint256 _shares,
        uint256 _totalShares,
        uint256 _totalBalance
    ) internal pure returns (uint256) {
        uint256 tokenOfShares;

        if (_totalShares == 0 || _totalBalance == 0) {
            tokenOfShares = 0;
        } else {
            tokenOfShares = (_shares * _totalBalance) / _totalShares;
        }

        return tokenOfShares;
    }

    function divCeil(uint256 x, uint256 y) internal pure returns (uint256) {
        uint256 remainder = x % y;
        uint256 result;

        if (remainder == 0) {
            result = x / y;
        } else {
            result = ((x - remainder) + y) / y;
        }

        return result;
    }
}

